mapper.select:
  	select
  	
    from up_aiinfo where 1=1
    
      and up_aicreatetime  >=  #{beginTime}
    
    
      and up_aicreatetime  <=  #{endTime}
    
    
      and up_usercode = #{userCode}
    
    and up_aistatus = '2'
    UNION
    select
    
    from up_aiinfo where 1=1
    
      and up_aicreatetime  >=  #{beginTime}
    
    
      and up_aicreatetime  <=  #{endTime}
    
    
      and up_usercode = #{userCode}
    
    and up_aistatus  <>  '2'
    and DATEDIFF(
		NOW(),
		up_aicreatetime
	) < 14

mapper.update:
update up_aiinfo
set up_aitaskid = #{upClueTaskid}
where up_usercode = #{userCode}
and up_aicode = #{upClueCode}
