mapper.select:
	select
	up_aicode, up_aiappraise, up_aistatus, 
    up_usercode, up_aitimes, up_aicreatetime, 
    up_aiexecutetime, up_aipersontime, up_aitaskid, 
    up_aiphone, extra1, extra2, 
    extra3, extra4, extra5
  from up_aiInfo where 1=1
  
    and up_aicreatetime  >=  #{beginTime}
  
  
    and up_aicreatetime  <=  #{endTime}
  
  
    and up_aistatus = #{status}
  
  
    and up_aitimes = #{times}
  
  
    and up_usercode = #{userCode}
  
  order by up_aitimes asc
