mapper.select:
select

from video_order
where state=1 and openid = #{openid,jdbcType=VARCHAR}

select 

from video_order where state=1 and head_img=#{userCode}

  and create_time  >=  #{startTime}


  and create_time  <=  #{endTime}


  and video_title = #{costType}

order by create_time desc

select cast(sum(total_fee) AS decimal(15,2)) from video_order where state='1'
and notify_time >= #{startTime}
and notify_time <= #{endTime}

select

from video_order where state='1'

	and create_time  >= 
	#{startTime}


	and create_time  <= 
	#{endTime}


	and head_img = #{organName}


	and openid = #{orderNum}

order by create_time desc
